import React, { useState } from 'react';
import useFetch from '../hooks/useFetch';
import { BirthdayResponse } from '../types';
import Button from '@mui/material/Button';
import FavouriteList from './FavouriteList';

interface Props {
  date: Date;
}

const formatDate = (date: Date): string => {
  const month = date.getMonth() + 1; // getMonth() is zero-based
  const day = date.getDate();
  return `${month}/${day}`;
};

const BirthdayList: React.FC<Props> = ({ date }) => {
  const [favourites, setFavourites] = useState<string[]>([]);
  const formattedDate = formatDate(date);
  const { data, loading, error } = useFetch(
    `https://api.wikimedia.org/feed/v1/wikipedia/en/onthisday/births/${formattedDate}`
  );

  const addFavourite = (title: string) => {
    setFavourites([...favourites, title]);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  const birthdays = (data as BirthdayResponse).events;

  return (
    <div>
      <h3>Birthdays on {formattedDate}</h3>
      <ul>
        {birthdays.map((birthday, index) => (
          <li key={index}>
            {birthday.text}
            <Button onClick={() => addFavourite(birthday.pages[0].title)}>Add to Favourites</Button>
          </li>
        ))}
      </ul>
      <FavouriteList favourites={favourites} />
    </div>
  );
};

export default BirthdayList;
