import React, { useState } from 'react';
import { LocalizationProvider, DatePicker } from '@mui/x-date-pickers';
import AdapterDateFns from '@mui/x-date-pickers/AdapterDateFns';
import TextField from '@mui/material/TextField';
import './Calendar.css';

const Calendar: React.FC = () => {
  const [selectedDate, setSelectedDate] = useState<Date | null>(new Date());
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [favouriteBirthdays, setFavouriteBirthdays] = useState<string[]>([]);
  const birthdays: string[] = [
    "CarryMinati, Indian YouTuber",
    "Gustav Forsling, Swedish ice hockey player",
    "Shonica Wharton, Barbadian netball player",
    "Philippe Coutinho, Brazilian footballer",
    "Allie DiMeco, American actress and musician",
    "Avisail García, Venezuelan baseball player",
    "Jrue Holiday, American basketball player",
    "David Worrall, English footballer",
    "Emma Eliasson, Swedish ice hockey player",
    "Ibrahim Jeilan, Ethiopian runner",
    "Eren Derdiyok, Swiss footballer"
  ];

  const addToFavourites = (birthday: string) => {
    if (!favouriteBirthdays.includes(birthday)) {
      setFavouriteBirthdays([...favouriteBirthdays, birthday]);
    }
  };

  const filteredBirthdays = birthdays.filter(birthday =>
    birthday.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <div className="header">
        <h1>Birthdays Calendar</h1>
      </div>
      <div className="container">
        <div className="calendar-container">
          <LocalizationProvider dateAdapter={AdapterDateFns}>
            <DatePicker
              value={selectedDate}
              onChange={(newValue) => setSelectedDate(newValue)}
              renderInput={(params) => <TextField {...params} />}
            />
          </LocalizationProvider>
        </div>
        <div className="birthdays-container">
          <h2>Birthdays on {selectedDate?.toLocaleDateString()}</h2>
          <input
            type="text"
            className="search-input"
            placeholder="Search birthdays..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <ul className="list-group">
            {filteredBirthdays.map((birthday, index) => (
              <li key={index} className="list-group-item">
                {birthday}
                <button onClick={() => addToFavourites(birthday)}>ADD TO FAVOURITES</button>
              </li>
            ))}
          </ul>
        </div>
        <div className="favourites-container">
          <h2>Favourite Birthdays</h2>
          <ul className="list-group">
            {favouriteBirthdays.map((birthday, index) => (
              <li key={index} className="list-group-item">
                {birthday}
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 Birthdays Calendar. All rights reserved.</p>
      </div>
    </div>
  );
};

export default Calendar;
